mapper.insert:
INSERT INTO APPROVAL_DOCU(
	ADC_NO
	, EMP_NO
	, CATEGORY_NO
	, ADC_NAME
	, ADC_CONTENT
	, APRVER_EMP_NO
	, APRVER_STATUS
	, AGREE_EMP_NO
	, AGREE_STATUS
	, REF_EMP_NO
	, REF_STATUS
)
VALUES(
	APRV_DOCU_NO.NEXTVAL
	, #{empNo}
	, #{categoryNo}
	, #{adcName}
	, #{adcContent}
	, #{aprverEmpNo}
	, #{aprverStatus}
	, #{agreeEmpNo}
	, #{agreeStatus}
	, #{refEmpNo}
	, #{refStatus}
)

mapper.update:
UPDATE APPROVAL_DOCU
	SET APRVER_STATUS = #{aprverStatus}
WHERE ADC_NO = #{adcNo}
