mapper.insert:
INSERT INTO CALENDAR
(
	CAL_NO
	CATE_NO
	CAL_DATE
	TITLE
	CONTENT
	WRITER
	START_DATE
	END_DATE
)VALUES
(
	SEQ_CALENDAR_NO.NEXTVAL
	, #{calNo}
	, #{cateNo}
	, #{calDate}
	, #{title}
	, #{content}
	, #{writer}
	, #{stratDate}
	, #{endDate}
)

mapper.update:
UPDATE CALENDAR
	SET
		TITLE = #{title}
		, CATE_NO = #{cateNo}
		, START_DATE = #{stratDate}
		, END_DATE = #{endDate}
		, CONTENT = #{content}
		, MODIFY_DATE = SYSDATE
	WHERE CAL_NO = #{calNo}
