mapper.update:
UPDATE CALENDAR
	SET
		TITLE = #{title}
		, CATE_NO = #{cateNo}
		, CONTENT = #{content}
		, START_DATE = #{startDate}
		, END_DATE = #{endDate}
		, MODIFY_DATE = SYSDATE
	WHERE CAL_NO = #{calNo}

