mapper.insert:
INSERT INTO EMPLOYEE
(
	EMP_NO,
	ID,
	PWD,
	NICK,
	PHONE,
	EMAIL,
	ADDRESS,
	SS_NO,
	HIRE_DATE,
	SALARY,
	PROFILE_PATH,
	COMPANY_NO,
	DEPT_NO,
	RANK_NO
)
VALUES
(
	SEQ_EMPLOYEE_NO.NEXTVAL
	,#{id}
	,#{pwd} 
    ,#{nick}
    ,#{phone} 
    ,#{email}
    ,#{address} 
    ,#{ssNo}
    ,#{hireDate} 
    ,#{salary}
    ,#{profilePath}
    ,#{companyNo}
    ,#{deptNo}
    ,#{rankNo}
)

mapper.select:
SELECT COUNT(*)
	FROM EMPLOYEE
WHERE ID = #{memberId}

SELECT COUNT(*)
	FROM EMPLOYEE
WHERE NICK = #{memberNick}
