mapper.insert:
INSERT INTO MAIL(
		MAIL_NO
		, SEND
		, RECEIVE
		, TITLE
		, CONTENT
)
VALUES (
	SEQ_MAIL_NO.NEXTVAL
	, ${send}
	, ${receive}
	, ${title}
	, ${content}
)

mapper.update:
UPDATE MAIL_RECEIVE
SET STAR = ${star}
WHERE MAIL_NO = ${mailNo}

mapper.select:
SELECT STAR
FROM MAIL_RECEIVE
WHERE MAIL_NO=${mailNo}
