mapper.insert:
INSERT INTO MAIL(
		MAIL_NO
		, SEND
		, TITLE
		, CONTENT
)
VALUES (
	SEQ_MAIL_NO.NEXTVAL
	, #{send}
	, #{title}
	, #{content}
)

INSERT INTO MAIL_RECEIVE(
	MAIL_REC_NO
	, MAIL_NO
	, RECEIVE

		, REFERENCE

)
VALUES (
	SEQ_MAIL_RECEIVE_NO.NEXTVAL
	, SEQ_MAIL_NO.CURRVAL
	, #{receive}

		, #{reference}

)

INSERT INTO MAIL_ATT(
	MAIL_ATT_NO
	, MAIL_NO
	, ORIGIN_NAME
	, NAME
)
VALUES (
	SEQ_MAIL_ATT_NO.NEXTVAL
	, SEQ_MAIL_NO.CURRVAL
	, #{originName}
	, #{name}
)

