	AND REFERENCE=#{reference}

mapper.select:
SELECT STAR
FROM MAIL_RECEIVE
WHERE MAIL_NO=#{mailNo}

	AND RECEIVE=#{receive}
	AND REFERENCE IS NULL


	AND REFERENCE=#{reference}

mapper.update:
UPDATE MAIL_RECEIVE
SET READ_YN = 'Y'
WHERE MAIL_NO=#{mailNo}

	AND RECEIVE=#{receive}
	AND REFERENCE IS NULL


	AND REFERENCE=#{reference}

UPDATE MAIL_RECEIVE
SET STATUS = #{status}
WHERE MAIL_NO = #{mailNo}

	AND RECEIVE = #{receive}
	AND REFERENCE IS NULL


	AND REFERENCE = #{reference}

