mapper.insert:
INSERT INTO MAIL(
		MAIL_NO
		, TITLE
		, CONTENT
)
VALUES (
	SEQ_MAIL_NO.NEXTVAL
	, #{title}
	, #{content}
)

INSERT INTO MAIL_RECEIVE(
	MAIL_REC_NO
	, MAIL_NO
	, SEND
	, RECEIVE

		, REFERENCE

)
VALUES (
	SEQ_MAIL_RECEIVE_NO.NEXTVAL
	, SEQ_MAIL_NO.CURRVAL
	, ${send}
	, ${receive}

		, ${reference}

)

INSERT INTO MAIL_ATT(
	MAIL_ATT_NO
	, MAIL_NO
	, ORIGIN_NAME
	, NAME
)
VALUES (
	SEQ_MAIL_ATT_NO.NEXTVAL
	, SEQ_MAIL_NO.CURRVAL
	, #{originName}
	, #{name}
)

mapper.update:
UPDATE MAIL_RECEIVE
SET STAR = ${star}
WHERE MAIL_NO=${mailNo}

	AND RECEIVE=${receive}
	AND REFERENCE IS NULL


	AND REFERENCE=${reference}

mapper.select:
SELECT STAR
FROM MAIL_RECEIVE
WHERE MAIL_NO=${mailNo}

	AND RECEIVE=${receive}
	AND REFERENCE IS NULL


	AND REFERENCE=${reference}

mapper.update:
UPDATE MAIL_RECEIVE
SET READ_YN = 'Y'
WHERE MAIL_NO=${mailNo}

	AND RECEIVE=${receive}
	AND REFERENCE IS NULL


	AND REFERENCE=${reference}

UPDATE MAIL
SET STATUS = 1
WHERE MAIL_NO = ${mailNo}
