 (
    REPLY_NO
    ,CONTENT
    ,BNO
    ,WRITER
 )
 VALUES
 (
    SEQ_NOTICE_REPLY_NO.NEXTVAL
    , #{content}
    , #{bno}
    , #{writer}
 )

mapper.update:
	UPDATE NOTICE_REPLY
		SET 
			 CONTENT = #{content}
            , ENROLL_DATE = SYSDATE
			, MODIFY_DATE = SYSDATE
	WHERE REPLY_NO = #{replyNo}
